import React from "react";
import Header from "./components/Header";
import Users from "./components/Users";
import AddUser from "./components/AddUser";

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
        users : [
            {
                id: 1,
                firstname: 'Bob',
                lastname: 'marley',
                bio: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Nisi eligendi reiciendis atque libero assumenda dolorem sunt nam omnis aliquid deserunt ab obcaecati, eius numquam odit fugit natus mollitia eos ipsum.',
                age: 40,
                isHappy: true
            },
            {
                id: 2,
                firstname: 'john',
                lastname: 'Doe',
                bio: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Nisi eligendi reiciendis atque libero assumenda dolorem sunt nam omnis aliquid deserunt ab obcaecati, eius numquam odit fugit natus mollitia eos ipsum.',
                age: 22,
                isHappy: false
            }
        ],
    }
    this.addUser = this.addUser.bind(this);
    this.deleteUser = this.deleteUser.bind(this);
}
  render () {
    return (<div>
        <Header title="list user" />
        <main>
          <Users users={this.state.users}/>
        </main>
        <aside>
          <AddUser onAdd={this.addUser}/>
        </aside>
      </div>)
  }

