  }

  addUser(user) {
    //console.log(user)
    const id = this.state.users.length + 1;
    // меняем список юзер , к текущему списку добавляем новый обьект из параметров
    this.setState({users: [...this.state.users, {id, ...user}] })
  }
}

export default App;
