import React from "react";

class Users extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            users : [
                {
                    id: 1,
                    firstname: 'Bob',
                    lastname: 'marley',
                    bio: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Nisi eligendi reiciendis atque libero assumenda dolorem sunt nam omnis aliquid deserunt ab obcaecati, eius numquam odit fugit natus mollitia eos ipsum.',
                    age: 40,
                    isHappy: true
                },
                {
                    id: 2,
                    firstname: 'john',
                    lastname: 'Doe',
                    bio: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Nisi eligendi reiciendis atque libero assumenda dolorem sunt nam omnis aliquid deserunt ab obcaecati, eius numquam odit fugit natus mollitia eos ipsum.',
                    age: 22,
                    isHappy: false
                }
            ],
        }
    }
    render () {
        if(this.state.users.length > 0) 
            return (<div>
                {this.state.users.map((el) => (<div className="user" key={el.id}>
                    <h3>{el.firstname} {el.lastname}</h3>
                    <p>{el.bio}</p>
                    <b>{el.isHappy ? 'happyes))' : 'not sure'}</b>
                </div>))}
            </div>)
        else
            return (<div className="user">
            <h3>user empty</h3>
           </div>)
    }

}

export default Users;
